#include <SFML/Graphics.hpp>\n\nint main() {\n    sf::RenderWindow window(sf::VideoMode(200, 200), "Keyboard Input");\n    while (window.isOpen()) {\n        sf::Event event;\n        while (window.pollEvent(event)) {\n            if (event.type == sf::Event::KeyPressed) {\n                std::cout << "You pressed: " << event.key.code << std::endl;\n            }\n        }\n    }\n    return 0;\n}