#include <Windows.h>\n#include <iostream>\n\nint main() {\n    const char* exePath = "path/to/executable.exe";\n    HMODULE hModule = LoadLibraryEx(exePath, NULL, LOAD_LIBRARY_AS_DATAFILE);\n    if (hModule == NULL) {\n        std::cerr << "Error loading executable" << std::endl;\n        return 1;\n    }\n\n    DWORD dwSize = GetModuleFileNameEx(hModule, hModule, NULL, 0);\n    if (dwSize == 0) {\n        std::cerr << "Error getting module file name" << std::endl;\n        FreeLibrary(hModule);\n        return 1;\n    }\n\n    char* dllName = new char[dwSize];\n    GetModuleFileNameEx(hModule, hModule, dllName, dwSize);\n    std::cout << "DLL dependencies:" << std::endl;\n    while (GetModuleFileNameEx(hModule, hModule, dllName, dwSize) != 0) {\n        std::cout << dllName << std::endl;\n        dwSize = GetModuleFileNameEx(hModule, hModule, NULL, 0);\n        delete[] dllName;\n        dllName = new char[dwSize];\n    }\n    delete[] dllName;\n    FreeLibrary(hModule);\n    return 0;\n}