#include <Windows.h>\n\nbool CheckRegistryValue(const char* key, const char* valueName) {\n    HKEY hKey;\n    LONG lRes = RegOpenKeyEx(HKEY_LOCAL_MACHINE, key, 0, KEY_READ, &hKey);\n    if (lRes != ERROR_SUCCESS) {\n        return false;\n    }\n\n    DWORD dwBufferSize = 1024;\n    char* lpBuffer = new char[dwBufferSize];\n    lRes = RegQueryValueEx(hKey, valueName, NULL, NULL, (LPBYTE)lpBuffer, &dwBufferSize);\n    delete[] lpBuffer;\n\n    RegCloseKey(hKey);\n\n    return (lRes == ERROR_SUCCESS);\n}\n\nint main() {\n    const char* key = "SOFTWARE\\YourCompany\\YourApp";\n    const char* valueName = "YourValue";\n\n    if (CheckRegistryValue(key, valueName)) {\n        printf("Registry value exists\n");\n    } else {\n        printf("Registry value does not exist\n");\n    }\n\n    return 0;\n}