// mymodel.h\nclass MyModel : public QObject {\npublic:\n    MyModel(QObject* parent = nullptr);\n    ~MyModel();\n\n    FooTableModel* getModel() const;\n\nprivate:\n    FooTableModel* m_model;\n};\n\n// mymodel.cpp\nMyModel::MyModel(QObject* parent)\n    : QObject(parent)\n    , m_model(new FooTableModel(this))\n{\n}\n\nMyModel::~MyModel()\n{\n    delete m_model;\n}\n\nFooTableModel* MyModel::getModel() const\n{\n    return m_model;\n}