// c++_code.cpp\n#include <libguile.h>\n\nint main() {\n    // Initialize Guile\n    scm_init_guile();\n\n    // Load Scheme code\n    scm_c_eval_string("(load \"scheme_code.scm\")");\n\n    // Call Scheme function\n    SCM result = scm_c_eval_string("(add 2 3)");\n\n    // Convert result to C type\n    int result_int = scm_to_int(result);\n\n    // Print result\n    std::cout << "Result: " << result_int << std::endl;\n\n    return 0;\n}