#include <iostream>\n#include <string>\n#include <cstdlib>\n\nint main() {\n    std::string revision;\n    FILE* pipe = popen("svnversion -n", "r");\n    if (!pipe) return 1;\n    char buffer[128];\n    while (!feof(pipe)) {\n        if (fgets(buffer, 128, pipe) != NULL)\n            revision += buffer;\n    }\n    pclose(pipe);\n    std::cout << "Revision: " << revision << std::endl;\n    return 0;\n}