// tess_jni.c (JNI wrapper for Tesseract)\n#include <jni.h>\n#include <tesseract/baseapi.h>\n\nJNIEXPORT jstring JNICALL Java_TesseractNative_doOCR(JNIEnv *env, jobject obj, jbyteArray image) {\n    // Initialize Tesseract\n    tesseract::TessBaseAPI *api = new tesseract::TessBaseAPI();\n    api->Init(NULL, "eng");\n\n    // Convert Java byte array to C++ unsigned char array\n    unsigned char *imageData = (unsigned char *)env->GetByteArrayElements(image, NULL);\n\n    // Perform OCR\n    api->SetImage(imageData, env->GetArrayLength(image));\n    char *text = api->GetUTF8Text();\n\n    // Clean up\n    delete [] text;\n    delete api;\n\n    // Return the OCR text as a Java string\n    return env->NewStringUTF(text);\n}