// Original interface\ninterface IMyEvents\n{\n    HRESULT FooCallback(...);\n    HRESULT BarCallback(...);\n};\n\n// Vtable layout:\n// 0x00: FooCallback\n// 0x04: BarCallback\n\n// Modified interface with new method inserted in the middle\ninterface IMyEvents\n{\n    HRESULT FooCallback(...);\n    HRESULT NewCallback(...);\n    HRESULT BarCallback(...);\n};\n\n// Vtable layout:\n// 0x00: FooCallback\n// 0x04: NewCallback\n// 0x08: BarCallback\n\n// Client code compiled against original interface\nvoid clientCode(IMyEvents* pEvents)\n{\n    // Calls FooCallback at index 0x00\n    pEvents->FooCallback(...);\n\n    // Calls BarCallback at index 0x04 (oops, now points to NewCallback!)\n    pEvents->BarCallback(...);\n}