// assuming pConnectionPoint is a valid connection point\nHRESULT FireEvent()\n{\n    HRESULT hr = E_FAIL;\n    IUnknown* pUnknown = NULL;\n\n    // Get the event interface\n    hr = pConnectionPoint->QueryInterface(IID_IEventInterface, (void**)&pUnknown);\n    if (SUCCEEDED(hr))\n    {\n        IEventInterface* pEventInterface = (IEventInterface*)pUnknown;\n        hr = pEventInterface->MyEvent(); // fire the event\n        pEventInterface->Release();\n    }\n\n    return hr;\n}