// mymodel.h\nclass MyModel : public QObject {\npublic:\n    MyModel(QObject* parent = nullptr);\n    ~MyModel();\n\n    FooTableModel* getModel() const;\n\nprivate:\n    std::unique_ptr<FooTableModel> m_model;\n};\n\n// mymodel.cpp\nMyModel::MyModel(QObject* parent)\n    : QObject(parent)\n    , m_model(std::make_unique<FooTableModel>(this))\n{\n}\n\nMyModel::~MyModel()\n{\n    // no need to delete m_model explicitly\n}\n\nFooTableModel* MyModel::getModel() const\n{\n    return m_model.get();\n}