// Before your main loop\nIDebugControl* debugControl = ...; // Get your IDebugControl instance\nIDebugClient* debugClient = ...; // Get your IDebugClient instance\n\n// ...\n\nwhile (true) {\n    // Your main loop\n    if (debugControl->WaitForEvent(DEBUG_WAIT_DEFAULT, INFINITE) != S_OK) {\n        // Handle error\n        break;\n    }\n\n    // ... (rest of your loop)\n\n    // Before calling SetExecutionStatus, make sure to sync the debug control\n    debugControl->Flush();\n    debugClient->Flush();\n\n    // Now you can safely call SetExecutionStatus\n    debugControl->SetExecutionStatus(DEBUG_STATUS_GO);\n}