#include <windows.h>\n#include <mscoree.h>\n\nint main()\n{\n    // Bind to the .NET runtime\n    ICLRRuntimeHost* pRuntimeHost = NULL;\n    HRESULT hr = CorBindToRuntimeEx(NULL, L"wks", 0, CLSID_CLRRuntimeHost, IID_PPV_ARGS(&pRuntimeHost));\n    if (FAILED(hr))\n    {\n        return 1;\n    }\n\n    // Load the managed DLL\n    HINSTANCE lib = LoadLibraryEx("dllcode.dll", NULL, LOAD_WITH_ALTERED_SEARCH_PATH);\n    if (!lib)\n    {\n        pRuntimeHost->Release();\n        return 1;\n    }\n\n    // Release the runtime host\n    pRuntimeHost->Release();\n\n    return 0;\n}