#include <iostream>\n#include <string>\n\nstd::string compress(const std::string& input) {\n    if (input.empty()) return "";\n\n    std::string compressed;\n    char current = input[0];\n    int count = 1;\n\n    for (int i = 1; i <= input.size(); ++i) {\n        if (i == input.size() || input[i] != current) {\n            compressed += current;\n            if (count > 1) {\n                compressed += std::to_string(count);\n            }\n            if (i < input.size()) {\n                current = input[i];\n                count = 1;\n            }\n        } else {\n            count++;\n        }\n    }\n\n    return compressed;\n}\n\nint main() {\n    std::string input = "d+c+d+f+d+c+d+f+d+c+d+f+d+c+d+f+";\n    std::string compressed = compress(input);\n    std::cout << compressed << std::endl;\n    return 0;\n}