#include <Windows.h>\n\nint main() {\n    SC_HANDLE hSCManager = OpenSCManager(NULL, NULL, SC_MANAGER_CONNECT);\n    if (hSCManager == NULL) {\n        // handle error\n    }\n\n    SC_HANDLE hService = OpenService(hSCManager, "YourServiceName", SERVICE_CHANGE_CONFIG);\n    if (hService == NULL) {\n        // handle error\n    }\n\n    // define the new logon credentials\n    wchar_t username[] = L"DOMAIN\\Username";\n    wchar_t password[] = L"Password";\n\n    SERVICE_DESCRIPTION sd;\n    sd.lpDescription = NULL;\n\n    ChangeServiceConfig(hService, SERVICE_NO_CHANGE, SERVICE_NO_CHANGE, SERVICE_NO_CHANGE, NULL, NULL, NULL, NULL, username, password, NULL, &sd);\n\n    CloseServiceHandle(hService);\n    CloseServiceHandle(hSCManager);\n\n    return 0;\n}