#include <Windows.h>\n\nint main() {\n    // Create a new console window\n    HWND hWnd = CreateWindow("ConsoleWindowClass", "Console Window 2", WS_OVERLAPPEDWINDOW, CW_USEDEFAULT, CW_USEDEFAULT, CW_USEDEFAULT, CW_USEDEFAULT, NULL, NULL, NULL, NULL);\n    ShowWindow(hWnd, SW_SHOW);\n\n    // Get the handle to the new console window\n    HANDLE hCon2 = CreateConsoleScreenBuffer(GENERIC_READ | GENERIC_WRITE, 0, NULL, CONSOLE_TEXTMODE_BUFFER, NULL);\n    SetConsoleActiveScreenBuffer(hCon2);\n\n    // Print to the original console window\n    printf("1\n");\n\n    // Print to the new console window\n    WriteConsole(hCon2, "2\n", 2, NULL, NULL);\n\n    // Don't forget to close the handle\n    CloseHandle(hCon2);\n\n    return 0;\n}