#include <Windows.h>\n\nint main() {\n    // Create a new console window\n    STARTUPINFO si;\n    PROCESS_INFORMATION pi;\n    ZeroMemory(&si, sizeof(si));\n    si.cb = sizeof(si);\n    ZeroMemory(&pi, sizeof(pi));\n    CreateProcess(NULL, "cmd.exe", NULL, NULL, FALSE, CREATE_NEW_CONSOLE, NULL, NULL, &si, &pi);\n\n    // Print to the original console window\n    printf("1\n");\n\n    // Print to the new console window\n    // You can use the handle to the new console window to print to it\n    // For simplicity, let's just print to the new console window using system function\n    system("echo 2 > con2");\n\n    // Don't forget to close the handles\n    CloseHandle(pi.hProcess);\n    CloseHandle(pi.hThread);\n\n    return 0;\n}