#include <iostream>\nusing namespace std;\n\nvoid findSubsets(int arr[], int n, int index = 0, string current = "") {\n    if (index == n) {\n        cout << current << endl;\n        return;\n    }\n\n    findSubsets(arr, n, index + 1, current);\n    findSubsets(arr, n, index + 1, current + to_string(arr[index]) + " ");\n}\n\nint main() {\n    int arr[] = {1, 2, 3, 4, 5};\n    int n = sizeof(arr) / sizeof(arr[0]);\n\n    findSubsets(arr, n);\n\n    return 0;\n}