#include <connmgr.h>\n\nint WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPWSTR lpCmdLine, int nCmdShow)\n{\n    // Initialize the Connection Manager\n    ConnMgrInit();\n\n    // Enumerate all connections\n    CONNMGR_CONNECTION_INFO *pConnInfo;\n    DWORD dwSize = 0;\n    ConnMgrEnumConnections(NULL, &dwSize);\n    pConnInfo = (CONNMGR_CONNECTION_INFO *)malloc(dwSize);\n    ConnMgrEnumConnections(pConnInfo, &dwSize);\n\n    // Release each GPRS connection\n    for (DWORD i = 0; i < dwSize; i++)\n    {\n        if (pConnInfo[i].dwType == CONNMGR_CONNECTION_TYPE_GPRS)\n        {\n            ConnMgrReleaseConnection(pConnInfo[i].hConnection);\n        }\n    }\n\n    // Free the connection info array\n    free(pConnInfo);\n\n    // Uninitialize the Connection Manager\n    ConnMgrUninit();\n\n    return 0;\n}