#include <iostream>\n\nvoid extractTimeComponents(uint64_t dateTime, int &hours, int &minutes, int &seconds, int &milliseconds) {\n    // Assuming the format is always yyyymmddHHMMSSmmm (year, month, day, hour, minute, second, millisecond)\n    uint64_t remaining = dateTime;\n\n    // Remove the year, month, and day part\n    remaining %= 10000000000; // Keep the last 11 digits (HHMMSSmmm)\n\n    // Extract hours\n    hours = (remaining / 100000000); // First 2 digits\n    remaining %= 100000000; // Remove hours\n\n    // Extract minutes\n    minutes = (remaining / 1000000); // Next 2 digits\n    remaining %= 1000000; // Remove minutes\n\n    // Extract seconds\n    seconds = (remaining / 1000); // Next 3 digits\n    remaining %= 1000; // Remove seconds\n\n    // The remainder is milliseconds\n    milliseconds = remaining;\n}\n\nint main() {\n    uint64_t utcDateTime = 20090520145024798;\n    int hours, minutes, seconds, milliseconds;\n\n    extractTimeComponents(utcDateTime, hours, minutes, seconds, milliseconds);\n\n    std::cout << "Hours: " << hours << std::endl;\n    std::cout << "Minutes: " << minutes << std::endl;\n    std::cout << "Seconds: " << seconds << std::endl;\n    std::cout << "Milliseconds: " << milliseconds << std::endl;\n\n    return 0;\n}