#include <aqtime.h>\n\nint main() {\n    // Initialize AQtime\n    AQTInitialize();\n\n    // Enable line profiling\n    AQTSetProfilingMode(AQT_PROFILE_LINES);\n\n    // Set the profiling level to detailed\n    AQTSetProfilingLevel(AQT_PROFILE_DETAILED);\n\n    // Enable function call tracing\n    AQTEnableTracing(AQT_TRACE_FUNCTION_CALLS);\n\n    // Run your code here...\n\n    // Finalize AQtime\n    AQTFinalize();\n\n    return 0;\n}