#include <iostream>\n#include <unistd.h>\n#include <sys/types.h>\n#include <sys/wait.h>\n\nint shared = 0;\n\nint main() {\n    pid_t pid = fork();\n\n    if (pid == 0) {\n        // Child process\n        shared++;\n        std::cout << "Child: shared = " << shared << ", &shared = " << &shared << std::endl;\n        exit(0);\n    } else {\n        // Parent process\n        shared++;\n        std::cout << "Parent: shared = " << shared << ", &shared = " << &shared << std::endl;\n        waitpid(pid, NULL, 0);\n    }\n\n    return 0;\n}