// Sample code to illustrate the registry key path\nHKEY hKey;\nLONG lRes = RegOpenKeyEx(HKEY_CLASSES_ROOT, TEXT("TypeLib\\<TypeLibID>\\1.0\\FLAGS"), 0, KEY_READ, &hKey);\nif (lRes == ERROR_SUCCESS) {\n    DWORD dwFlags;\n    RegQueryValueEx(hKey, NULL, NULL, NULL, (LPBYTE)&dwFlags, sizeof(dwFlags));\n    // Check the dwFlags value to determine the threading model\n    RegCloseKey(hKey);\n}