#include <Windows.h>\n#include <GL/GL.h>\n\nint main() {\n    HDC hdc = GetDC(NULL);\n    PIXELFORMATDESCRIPTOR pfd = { 0 };\n    pfd.nSize = sizeof(PIXELFORMATDESCRIPTOR);\n    pfd.nVersion = 1;\n    pfd.dwFlags = PFD_SUPPORT_OPENGL | PFD_DOUBLEBUFFER;\n    pfd.iPixelType = PFD_TYPE_RGBA;\n    pfd.cColorBits = 32;\n    pfd.cDepthBits = 24;\n    pfd.cStencilBits = 8;\n\n    int pf = ChoosePixelFormat(hdc, &pfd);\n    SetPixelFormat(hdc, pf, &pfd);\n\n    HGLRC hrc = wglCreateContext(hdc);\n    wglMakeCurrent(hdc, hrc);\n\n    int acceleration;\n    wglGetInfo(hdc, WGL_INFO_ACCELERATION, &acceleration);\n\n    if (acceleration == WGL_NO_ACCELERATION) {\n        // Hardware acceleration is disabled\n    } else if (acceleration == WGL_GENERIC_ACCELERATION) {\n        // Hardware acceleration is enabled, but in a generic mode\n    } else if (acceleration == WGL_FULL_ACCELERATION) {\n        // Hardware acceleration is fully enabled\n    }\n\n    wglDeleteContext(hrc);\n    ReleaseDC(NULL, hdc);\n    return 0;\n}