#include <windows.h>\n\n// Define the COM interface\nEXTERN_C const GUID CLSID_IReadStream = { 0x... };\n\n// Define the native implementation\nint read(Pointer buffer, int offset, int count) {\n    // Call the C# implementation via COM\n    IReadStream* pStream = NULL;\n    HRESULT hr = CoCreateInstance(CLSID_IReadStream, NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&pStream));\n    if (SUCCEEDED(hr)) {\n        int bytesRead = pStream->Read(buffer, offset, count);\n        pStream->Release();\n        return bytesRead;\n    } else {\n        return -1;\n    }\n}