// In the EXE file\n__declspec(dllexport) void ExeMain()\n{\n    // Initialize the EXE's resources, perform startup tasks, etc.\n}\n\n// In the main application\nHMODULE hExeModule = LoadLibrary("path/to/exe.exe");\nif (hExeModule != NULL)\n{\n    typedef void (*ExeMainFunc)();\n    ExeMainFunc pExeMain = (ExeMainFunc)GetProcAddress(hExeModule, "ExeMain");\n    if (pExeMain != NULL)\n    {\n        pExeMain(); // Call the ExeMain function\n    }\n    FreeLibrary(hExeModule);\n}