#include <iostream>\n\nbool is_prime(int num) {\n  if (num <= 1) return false;\n  for (int i = 2; i * i <= num; i++) {\n    if (num % i == 0) return false;\n  }\n  return true;\n}\n\nint main() {\n  int limit = 1000000;\n  int sum = 0;\n  for (int i = 2; i <= limit; i++) {\n    if (is_prime(i)) sum += i;\n  }\n  std::cout << "Sum of prime numbers up to " << limit << ": " << sum << std::endl;\n  return 0;\n}