// assuming you have a 2D texture with height 'h'\nGLfloat texture_coords[] = {\n    0.0f, 1.0f,  // top-left\n    1.0f, 1.0f,  // top-right\n    1.0f, 0.0f,  // bottom-right\n    0.0f, 0.0f   // bottom-left\n};\n\n// flip the y-coordinate\nfor (int i = 1; i < 8; i += 2) {\n    texture_coords[i] = 1.0f - texture_coords[i];\n}\n\n// use the modified texture coordinates\nglTexCoordPointer(2, GL_FLOAT, 0, texture_coords);