#include <IESecurity.H>\n\nint GetIEZone()\n{\n    IESecurity* pIESecurity = NULL;\n    HRESULT hr = CoCreateInstance(CLSID_IESecurity, NULL, \n                                  CLSCTX_ALL, IID_IESecurity, \n                                  (void**)&pIESecurity);\n    if (SUCCEEDED(hr))\n    {\n        DWORD dwZone = 0;\n        hr = pIESecurity->GetZone(&dwZone);\n        if (SUCCEEDED(hr))\n        {\n            return dwZone;\n        }\n        pIESecurity->Release();\n    }\n    return -1; // error\n}