// mytemplate.h\ntemplate <typename T>\nclass MyTemplate {\npublic:\n    static T& getStaticVar() {\n        static T instance; // This will be instantiated separately in each DLL\n        return instance;\n    }\n};\n\n// mytemplate.cpp (in one of the DLLs)\ntemplate class MyTemplate<int>; // Explicit instantiation\n__declspec(dllexport) int& getStaticVarInt() {\n    return MyTemplate<int>::getStaticVar();\n}\n\n// otherdll.cpp (in another DLL)\n__declspec(dllimport) int& getStaticVarInt();