#include <windows.h>\n#include <ole2.h>\n\n// Load the COM DLL\nCoInitialize(NULL);\nIDispatch* comObj = NULL;\nHRESULT hr = CoCreateInstance(&CLSID_Your_COM_Class_Name, NULL, CLSCTX_INPROC_SERVER, IID_IDispatch, (void**)&comObj);\n\n// Call a method on the COM object\nDISPID dispid = 0;\nOLECHAR* methodName = L"YourMethod";\nhr = comObj->GetIDsOfNames(IID_NULL, &methodName, 1, LOCALE_SYSTEM_DEFAULT, &dispid);\nif (SUCCEEDED(hr)) {\n    VARIANT result;\n    hr = comObj->Invoke(dispid, IID_NULL, LOCALE_SYSTEM_DEFAULT, DISPATCH_METHOD, NULL, &result, NULL, NULL);\n}