#include <Windows.h>\n\nint main() {\n    // Open the file for writing\n    HANDLE hFile = CreateFileA("large_file.dat", GENERIC_WRITE, 0, NULL, CREATE_ALWAYS, FILE_ATTRIBUTE_NORMAL, NULL);\n    if (hFile == INVALID_HANDLE_VALUE) {\n        // Handle error\n    }\n\n    // Set the file size to 1 GB (1024*1024*1024 bytes)\n    LARGE_INTEGER fileSize;\n    fileSize.QuadPart = 1024 * 1024 * 1024;\n    if (!SetFileValidData(hFile, fileSize.LowPart, fileSize.HighPart)) {\n        // Handle error\n    }\n\n    // Close the file handle\n    CloseHandle(hFile);\n    return 0;\n}