#include <vector>\n#include <cmath>\n\nstruct Point {\n    int x, y;\n};\n\nstd::vector<Point> getPointsWithinRadius(const Point& center, int radius, const std::vector<Point>& points) {\n    std::vector<Point> result;\n    for (const auto& point : points) {\n        int dx = point.x - center.x;\n        int dy = point.y - center.y;\n        int distance = std::sqrt(dx * dx + dy * dy);\n        if (distance <= radius) {\n            result.push_back(point);\n        }\n    }\n    return result;\n}