// Killer move heuristic\nMove killerMove = NULL_MOVE;\n\nvoid alphaBetaSearch(Board* board, int depth, int alpha, int beta) {\n    // ...\n    if (killerMove != NULL_MOVE) {\n        // Try the killer move first\n        board->makeMove(killerMove);\n        int score = -alphaBetaSearch(board, depth - 1, -beta, -alpha);\n        board->undoMove(killerMove);\n        if (score >= beta) {\n            // beta cutoff, return score\n            return score;\n        }\n    }\n    // ...\n}