// Stub for openfile function\nbool openfileStub(const std::string& filename) {\n    // Return a fixed value for testing purposes\n    return true;\n}\n\n// Test case\nTEST_CASE("LoadFileToMem_OpenFileSuccess", "[LoadFileToMem]") {\n    // Arrange\n    std::string filename = "testfile.txt";\n    EXPECT_CALL(openfileStub, openfile(filename)).Times(1);\n\n    // Act\n    bool result = LoadFileToMem(filename);\n\n    // Assert\n    ASSERT_TRUE(result);\n}