#include <opencv2/opencv.hpp>\n\nint main() {\n  // Load the image\n  cv::Mat image = cv::imread("image.jpg");\n\n  // Convert the image to grayscale\n  cv::Mat gray;\n  cv::cvtColor(image, gray, cv::COLOR_BGR2GRAY);\n\n  // Apply edge detection using Canny algorithm\n  cv::Mat edges;\n  cv::Canny(gray, edges, 50, 150);\n\n  // Find contours of the objects in the image\n  std::vector<std::vector<cv::Point>> contours;\n  cv::findContours(edges, contours, cv::RETR_EXTERNAL, cv::CHAIN_APPROX_SIMPLE);\n\n  // Iterate through the contours and find the bounding rectangle for each object\n  for (const auto& contour : contours) {\n    cv::Rect boundingRect = cv::boundingRect(contour);\n    // You can now use the boundingRect to find the lowest and highest pixel coordinates of each object\n  }\n\n  return 0;\n}