#include <soci.h>\n#include <soci/sqlite3/soci-sqlite3.h>\n\nint main() {\n  // Create a SQLite database\n  soci::session sql(soci::sqlite3, "mydatabase.db");\n\n  // Create a table\n  sql << "CREATE TABLE mytable (id INTEGER PRIMARY KEY, name TEXT)";\n\n  // Insert data\n  sql << "INSERT INTO mytable (name) VALUES ('John')";\n\n  // Query data\n  soci::rowset<soci::row> rs = (sql.prepare << "SELECT * FROM mytable");\n\n  // Print results\n  for (auto& row : rs) {\n    std::cout << row["id"] << " " << row["name"] << std::endl;\n  }\n\n  return 0;\n}