#include <Windows.h>\n#include <Wtsapi32.h>\n\nint main() {\n    HANDLE hServer = WTSOpenServer(NULL);\n    if (hServer != NULL) {\n        DWORD sessionId = WTSGetActiveConsoleSessionId(hServer);\n        if (sessionId != 0xFFFFFFFF) {\n            WTSSendMessage(hServer, sessionId, "Ctrl+Alt+Delete", 0, TRUE);\n        }\n        WTSCloseServer(hServer);\n    }\n    return 0;\n}