#include <Windows.h>\n\nint main()\n{\n    // Load the DLL\n    HMODULE hModule = LoadLibraryEx("C:\\IETest\\debug\\IETest.dll", NULL, LOAD_LIBRARY_AS_DATAFILE);\n\n    if (hModule != NULL)\n    {\n        // Find the resource\n        HRSRC hResource = FindResource(hModule, MAKEINTRESOURCE(TEST), RT_HTML);\n\n        if (hResource != NULL)\n        {\n            // Load the resource\n            HGLOBAL hGlobal = LoadResource(hModule, hResource);\n\n            if (hGlobal != NULL)\n            {\n                // Lock the resource\n                LPVOID pData = LockResource(hGlobal);\n\n                if (pData != NULL)\n                {\n                    // You can now examine the resource data\n                    // For example, you can print the HTML content\n                    printf("%s", (char*)pData);\n\n                    // Don't forget to unlock the resource\n                    UnlockResource(hGlobal);\n                }\n\n                // Free the resource\n                FreeResource(hGlobal);\n            }\n        }\n\n        // Free the DLL\n        FreeLibrary(hModule);\n    }\n\n    return 0;\n}