#include <windows.h>\n#include <mmsystem.h>\n\nint main() {\n    // Get the number of recording devices\n    int numDevices = waveInGetNumDevs();\n\n    // Check if there are any recording devices\n    if (numDevices > 0) {\n        // Get the default recording device\n        WAVEINCAPS wc;\n        waveInGetDevCaps(WAVE_MAPPER, &wc, sizeof(WAVEINCAPS));\n\n        // Check if the default device is valid\n        if (wc.wMid == MMSYSERR_NOERROR) {\n            // Default recording device is present\n            printf("Default recording device is present\n");\n        } else {\n            // No default recording device is present\n            printf("No default recording device is present\n");\n        }\n    } else {\n        // No recording devices are present\n        printf("No recording devices are present\n");\n    }\n\n    return 0;\n}