#include <Windows.h>\n#include <winioctl.h>\n\nint main() {\n    HANDLE hDevice = CreateFileA("\\\\.\\PhysicalDrive0", GENERIC_READ, FILE_SHARE_READ | FILE_SHARE_WRITE, NULL, OPEN_EXISTING, 0, NULL);\n    if (hDevice == INVALID_HANDLE_VALUE) {\n        // Handle error\n    }\n\n    STORAGE_PROPERTY_QUERY query = {};\n    query.PropertyId = StorageDeviceProperty;\n    query.QueryType = PropertyStandardQuery;\n\n    STORAGE_DEVICE_DESCRIPTOR descriptor = {};\n    DWORD bytesReturned = 0;\n\n    if (!DeviceIoControl(hDevice, IOCTL_STORAGE_QUERY_PROPERTY, &query, sizeof(query), &descriptor, sizeof(descriptor), &bytesReturned, NULL)) {\n        // Handle error\n    }\n\n    // Access properties in the descriptor structure\n    printf("Device Type: %u\n", descriptor.DeviceType);\n    printf("Device Characteristics: %u\n", descriptor.DeviceCharacteristics);\n\n    CloseHandle(hDevice);\n    return 0;\n}