#include <Windows.h>\n#include <winioctl.h>\n\nint main() {\n    HANDLE hDevice = CreateFileA("\\\\.\\PhysicalDrive0", GENERIC_READ, FILE_SHARE_READ | FILE_SHARE_WRITE, NULL, OPEN_EXISTING, 0, NULL);\n    if (hDevice == INVALID_HANDLE_VALUE) {\n        // Handle error\n    }\n\n    ATA_PASS_THROUGH_EX apt = {};\n    apt.AtaFlags = ATA_FLAGS_DATA_IN | ATA_FLAGS_DRDY_REQUIRED;\n    apt.AtaCommand = ATA_IDENTIFY_DEVICE;\n    apt.DataTransferLength = 0x200;\n    apt.DataBuffer = (PVOID)malloc(0x200);\n\n    DWORD bytesReturned = 0;\n\n    if (!DeviceIoControl(hDevice, IOCTL_ATA_PASS_THROUGH, &apt, sizeof(apt), &apt, sizeof(apt), &bytesReturned, NULL)) {\n        // Handle error\n    }\n\n    // Access properties in the data buffer\n    PBYTE data = (PBYTE)apt.DataBuffer;\n    printf("Device Model: %.*s\n", 40, data + 54);\n\n    free(apt.DataBuffer);\n    CloseHandle(hDevice);\n    return 0;\n}