const int latchPin = 12;  // Pin connected to the latch pin of the shift registers\nconst int clockPin = 13;  // Pin connected to the clock pin of the shift registers\nconst int dataPin = 11;   // Pin connected to the data pin of the shift registers\n\nvoid setup() {\n  pinMode(latchPin, OUTPUT);\n  pinMode(clockPin, OUTPUT);\n  pinMode(dataPin, OUTPUT);\n}\n\nvoid loop() {\n  // Turn on the first 8 LEDs connected to the Blue 1-8 channels\n  shiftOut(dataPin, clockPin, MSBFIRST, 0xFF);  // Send 0xFF (all ones) to the first shift register\n  shiftOut(dataPin, clockPin, MSBFIRST, 0x00);  // Send 0x00 (all zeros) to the second shift register\n  shiftOut(dataPin, clockPin, MSBFIRST, 0x00);  // Send 0x00 (all zeros) to the third shift register\n  digitalWrite(latchPin, HIGH);  // Latch the data\n  delay(1000);\n\n  // Turn off the first 8 LEDs connected to the Blue 1-8 channels\n  shiftOut(dataPin, clockPin, MSBFIRST, 0x00);  // Send 0x00 (all zeros) to the first shift register\n  shiftOut(dataPin, clockPin, MSBFIRST, 0x00);  // Send 0x00 (all zeros) to the second shift register\n  shiftOut(dataPin, clockPin, MSBFIRST, 0x00);  // Send 0x00 (all zeros) to the third shift register\n  digitalWrite(latchPin, HIGH);  // Latch the data\n  delay(1000);\n}