#include "unity.h"\n#include "interrupt_handling.h"\n\nTEST_CASE("interrupt_prioritization") {\n    // Set up interrupt priorities\n    set_interrupt_priority(INTERRUPT_1, HIGH_PRIORITY);\n    set_interrupt_priority(INTERRUPT_2, LOW_PRIORITY);\n\n    // Trigger interrupts\n    trigger_interrupt(INTERRUPT_1);\n    trigger_interrupt(INTERRUPT_2);\n\n    // Verify that high-priority interrupt is handled first\n    TEST_ASSERT_EQUAL(HIGH_PRIORITY, get_interrupt_priority());\n}