// Example of using Poppler to render a PDF in C++\n#include <poppler-document.h>\n#include <poppler-page.h>\n\nint main() {\n  // Load the PDF\n  poppler::document *doc = poppler::document::load_from_file("path/to/pdf.pdf");\n\n  // Render the PDF\n  poppler::page *page = doc->create_page(0);\n  poppler::image img = page->render_to_image(150, 150);\n  // Display the rendered image\n  // ...\n\n  return 0;\n}