// Simplified example of RLE compression in firmware\nvoid compress_data(uint8_t* data, uint32_t length) {\n    uint8_t compressed_data[length];\n    uint32_t compressed_length = 0;\n\n    // Compress data using RLE\n    for (uint32_t i = 0; i < length; i++) {\n        uint8_t count = 1;\n        while (i + 1 < length && data[i] == data[i + 1]) {\n            i++;\n            count++;\n        }\n        compressed_data[compressed_length] = data[i];\n        compressed_data[compressed_length + 1] = count;\n        compressed_length += 2;\n    }\n\n    // Store compressed data on disk\n    write_to_disk(compressed_data, compressed_length);\n}\n\nvoid decompress_data(uint8_t* compressed_data, uint32_t compressed_length) {\n    uint8_t decompressed_data[compressed_length * 2];\n    uint32_t decompressed_length = 0;\n\n    // Decompress data using RLE\n    for (uint32_t i = 0; i < compressed_length; i += 2) {\n        uint8_t byte = compressed_data[i];\n        uint8_t count = compressed_data[i + 1];\n        for (uint32_t j = 0; j < count; j++) {\n            decompressed_data[decompressed_length] = byte;\n            decompressed_length++;\n        }\n    }\n\n    // Return decompressed data to the operating system\n    return decompressed_data;\n}