// Example of a data-only attack in C\n#include <stdio.h>\n#include <stdlib.h>\n#include <string.h>\n\nvoid vulnerable_function(char *data) {\n    // Assume data is a null-terminated string\n    char buffer[256];\n    strcpy(buffer, data); // Data-only vulnerability\n\n    // Process the data\n    printf("Processed data: %s\n", buffer);\n}\n\nint main() {\n    // Create a malicious input\n    char malicious_input[256];\n    memset(malicious_input, 'A', 256); // Fill with arbitrary data\n    malicious_input[255] = '\0'; // Null-terminate\n\n    // Call the vulnerable function with the malicious input\n    vulnerable_function(malicious_input);\n\n    return 0;\n}