// main.c\n#include <dlfcn.h>\n\nint main() {\n    void* handle = dlopen("user_module.so", RTLD_LAZY);\n    if (!handle) {\n        // error handling\n    }\n\n    // Get a function pointer from the loaded module\n    void (*user_func)() = dlsym(handle, "user_function");\n    if (!user_func) {\n        // error handling\n    }\n\n    // Call the user-provided function\n    user_func();\n\n    dlclose(handle);\n    return 0;\n}