// Example usage:\n#include "ppp.h"\n\nint main() {\n    // Initialize PPP connection\n    ppp_init(&ppp, "/dev/ttyS0", 115200);\n    // Establish connection\n    ppp_connect(&ppp);\n    // Send data\n    ppp_send(&ppp, "Hello, world!", 13);\n    // Receive data\n    char buffer[1024];\n    ppp_recv(&ppp, buffer, 1024);\n    // Close connection\n    ppp_disconnect(&ppp);\n    return 0;\n}