// Sender side\nchar data[] = "Hello, World!";\nint data_len = strlen(data);\nint mss = 5; // Small MSS for demonstration purposes\nint num_segments = (data_len + mss - 1) / mss;\n\nfor (int i = 0; i < num_segments; i++) {\n    int segment_len = (i == num_segments - 1) ? data_len % mss : mss;\n    send(tcp_socket, data + i * mss, segment_len, 0);\n}\n\n// Receiver side\nchar buffer[256];\nint bytes_received = 0;\n\nwhile (bytes_received < data_len) {\n    int bytes_read = recv(tcp_socket, buffer + bytes_received, 256 - bytes_received, 0);\n    if (bytes_read <= 0) {\n        // Handle error or disconnection\n    }\n    bytes_received += bytes_read;\n}\n\nprintf("%s\n", buffer); // Prints "Hello, World!"