// Calculate CRC value for file contents (excluding CRC value)\nuint32_t crc = crc_calculate(file_contents, file_length);\n\n// Append CRC value to end of file\nfwrite(&crc, sizeof(crc), 1, file_pointer);\n\n// Later, when verifying the file...\nuint32_t calculated_crc = crc_calculate(file_contents, file_length);\nuint32_t stored_crc;\nfread(&stored_crc, sizeof(stored_crc), 1, file_pointer);\n\nif (calculated_crc != stored_crc) {\n    // CRC mismatch, file may be corrupted\n}